import * as path from "path";

export const PLUGIN_NAME: string = "dllWebpackPlugin";

export const DLL_VERSION_JSON_NAME: string = "dll.version.json";

export const DEFAULT_PACKAGE_JSON_PATH: string = "./package.json";

export const NODE_ENV: string = process.env.NODE_ENV || "production";

export const PLUGIN_PATH: string = path.resolve(`./${PLUGIN_NAME}`);

export const MANIFEST_JSON_PATH: string = path.resolve(`./${PLUGIN_NAME}/${NODE_ENV}/[name].manifest.json`);

export const DLL_VERSION_JSON_PATH: string = path.resolve(`./${PLUGIN_NAME}/${DLL_VERSION_JSON_NAME}`);

export const DLL_PATH: string = path.resolve(`./${PLUGIN_NAME}/${NODE_ENV}`);

export const isProduction: boolean = NODE_ENV === "production";

export const DEFAULT_LIBRARY = "[name]_library_dll";

export const defaultDllVersionJson = {
    "name": PLUGIN_NAME,
    "onceForceUpdate": false,
    "alwayForceUpdate": false,
    "dllConfig": {
        "development": 0,
        "production": 0
    },
    "dllEntry": 0
}
